%Author: Yingpin Chen comes from Minnan Normal University%Copyright:Minnan Normal University%Date:2025.6.21%Function: Fast solver of Sylvester Equationclc;clear allm = 10; % 矩阵维度% A = eye(m); % 创建单位矩阵（主对角线为1）% A1 = fliplr(eye(m)); % 创建反对角线为1的矩阵% A = A | A1; % 逻辑或运算合并两个矩阵% A=double(A);D = diag(rand(m,1)); % 对角矩阵（显然可对角化）P = rand(m); % 随机可逆矩阵while rank(P) < m    P = rand(m); % 确保P满秩endA = P*D*P'; % 相似变换得到的可对角化矩阵figure(1)imagesc(A) % 显示结果矩阵colorbarn = 20; % 矩阵维度B = eye(n); % 创建单位矩阵（主对角线为1）B1 = fliplr(eye(n)); % 创建反对角线为1的矩阵B = B | B1; % 逻辑或运算合并两个矩阵B=double(B);figure(2)imagesc(B) % 显示结果矩阵colorbarX=double(eye(m));X1=X;X=[X,X1];X=double(X);figure(3)imagesc(X) % 显示结果矩阵colorbarY=A*X+X*B;figure(4)imagesc(Y) % 显示结果矩阵colorbarticSolution_X1=reshape( pinv(   kron(eye(n),A)+kron(B',eye(m))   )*Y(:)    ,[m,n]);tocfigure(5)imagesc(Solution_X1)colorbartic[U, Lambda, UH] = svd(A);  [V, Phi, VH] = svd(B); Q=repmat(diag(Lambda), 1, n)+(repmat(diag(Phi), 1, m)).';Solution_X2=U*((1./Q).*(U'*Y*(V)))*V';tocfigure(6)imagesc(Solution_X2)colorbarnorm(Solution_X2-Solution_X1,2)