%Author: Yingpin Chen comes from Minnan Normal University%Copyright:Minnan Normal University%Date:2025.6.21%Function: Fast solver of Sylvester Equationclc;clear allm = 50; % 矩阵维度Gamma=1e-10;A=circ_diff_matrix(m).'*circ_diff_matrix(m)+Gamma;  %rank(A)figure(1)imagesc(A) % 显示结果矩阵colorbarn = 100; % 矩阵维度B = eye(n); % 创建单位矩阵（主对角线为1）B1 = fliplr(eye(n)); % 创建反对角线为1的矩阵B = B | B1; % 逻辑或运算合并两个矩阵B=double(B);figure(2)imagesc(B) % 显示结果矩阵colorbarX=double(eye(m));X1=X;X=[X,X1];X=double(X);figure(3)imagesc(X) % 显示结果矩阵colorbarY=A*X+X*B;figure(4)imagesc(Y) % 显示结果矩阵colorbarticSolution_X1=reshape( inv(   kron(eye(n),A)+kron(B',eye(m))   )*Y(:)    ,[m,n]);tocfigure(5)imagesc(Solution_X1)colorbarticLambda=(fft(A(1,:))).';[V, Phi, VH] = svd(B); Q=repmat((Lambda), 1, n)+(repmat(diag(Phi), 1, m)).';Solution_X2=real(fft(((1./Q).*(ifft(Y*(V)))))*V.');tocfigure(6)imagesc(Solution_X2)colorbarPSNR_gt=MYpsnr(X,Solution_X2);SSIM_gt=MYssim(X,Solution_X2);LSE=norm(Solution_X2-Solution_X1,2)